// models/User.js
const { pool } = require('../config/db');
const bcrypt = require('bcryptjs');

const User = {
  findByEmail: async (email) => {
    const result = await pool.query('SELECT * FROM users WHERE email = $1', [email]);
    return result.rows[0];
  },
  findById: async (id) => {
    const result = await pool.query('SELECT * FROM users WHERE id = $1', [id]);
    return result.rows[0];
  },
  create: async (name, email, password) => {
    const hashedPassword = await bcrypt.hash(password, 10);
    const result = await pool.query(
      'INSERT INTO users (name, email, password, role, created_at, updated_at) VALUES ($1, $2, $3, $4, NOW(), NOW()) RETURNING *',
      [name, email, hashedPassword, 'user'] // Default role is 'user'
    );
    return result.rows[0];
  },
};

module.exports = User;
```

```javascript
// routes/postRoutes.js
const express = require('express');
const router = express.Router();
const Post = require('../models/Post');
const { protect } = require('../middleware/authMiddleware'); // Protects routes for logged-in users
const logger = require('../utils/logger');

// @desc    Get all posts
// @route   GET /api/posts
// @access  Public
router.get('/', async (req, res, next) => {
  try {
    const posts = await Post.getAll();
    res.json(posts);
  } catch (error) {
    logger.error(`Error getting posts: ${error.message}`);
    next(error); // Pass error to error handling middleware
  }
});

// @desc    Get post by ID
// @route   GET /api/posts/:id
// @access  Public
router.get('/:id', async (req, res, next) => {
  try {
    const post = await Post.getById(req.params.id);
    if (!post) {
      return res.status(404).json({ message: 'Post not found' });
    }
    res.json(post);
  } catch (error) {
    logger.error(`Error getting post by ID: ${error.message}`);
    next(error);
  }
});

// @desc    Create a post
// @route   POST /api/posts
// @access  Private (requires authentication)
router.post('/', protect, async (req, res, next) => {
  try {
    const { title, content } = req.body;
    const authorId = req.user.id; // Get user ID from the authenticated user
    const post = await Post.create(title, content, authorId);
    res.status(201).json(post); // 201 Created
  } catch (error) {
    logger.error(`Error creating post: ${error.message}`);
    next(error);
  }
});

// @desc    Update a post
// @route   PUT /api/posts/:id
// @access  Private (requires authentication)
router.put('/:id', protect, async (req, res, next) => {
  try {
    const { title, content } = req.body;
    const post = await Post.update(req.params.id, title, content);
    if (!post) {
      return res.status(404).json({ message: 'Post not found' });
    }
    res.json(post);
  } catch (error) {
    logger.error(`Error updating post: ${error.message}`);
    next(error);
  }
});

// @desc    Delete a post
// @route   DELETE /api/posts/:id
// @access  Private (requires authentication)
router.delete('/:id', protect, async (req, res, next) => {
  try {
    await Post.delete(req.params.id);
    res.status(204).send(); // 204 No Content (successful deletion)
  } catch (error) {
    logger.error(`Error deleting post: ${error.message}`);
    next(error);
  }
});

module.exports = router;
```

```javascript
// routes/authRoutes.js
const express = require('express');
const router = express.Router();
const bcrypt = require('bcryptjs');
const jwt = require('jsonwebtoken');
const User = require('../models/User');
const logger = require('../utils/logger');

// @desc    Register a new user
// @route   POST /api/auth/register
// @access  Public
router.post('/register', async (req, res, next) => {
  try {
    const { name, email, password } = req.body;

    // Check if user exists
    const userExists = await User.findByEmail(email);
    if (userExists) {
      return res.status(400).json({ message: 'User already exists' });
    }

    const user = await User.create(name, email, password);

    // Generate JWT
    const token = jwt.sign({ id: user.id, role: user.role }, process.env.JWT_SECRET, {
      expiresIn: '30d',
    });

    res.status(201).json({
      id: user.id,
      name: user.name,
      email: user.email,
      role: user.role,
      token: token,
    });
  } catch (error) {
    logger.error(`Error registering user: ${error.message}`);
    next(error);
  }
});

// @desc    Login user & get token
// @route   POST /api/auth/login
// @access  Public
router.post('/login', async (req, res, next) => {
  try {
    const { email, password } = req.body;

    // Check if user exists
    const user = await User.findByEmail(email);

    if (user && (await bcrypt.compare(password, user.password))) {
      // Generate JWT
      const token = jwt.sign({ id: user.id, role: user.role }, process.env.JWT_SECRET, {
        expiresIn: '30d',
      });

      res.json({
        id: user.id,
        name: user.name,
        email: user.email,
        role: user.role,
        token: token,
      });
    } else {
      res.status(401).json({ message: 'Invalid credentials' });
    }
  } catch (error) {
    logger.error(`Error logging in user: ${error.message}`);
    next(error);
  }
});

module.exports = router;
```

